const jwtSecret="your_jwt_secret",jwt=require("jsonwebtoken"),passport=require("passport");require("./passport");let generateJWTToken=user=>jwt.sign(user,jwtSecret,{subject:user.Username,expiresIn:"7d",algorithm:"HS256"});module.exports=router=>{router.post("/login",((req,res)=>{passport.authenticate("local",{session:!1},((error,user,info)=>{if(error||!user)return res.status(400).json({message:"Something is not right",user:user});req.login(user,{session:!1},(error=>{error&&res.send(error);let token=generateJWTToken(user.toJSON());return res.json({user:user,token:token})}))}))(req,res)}))};